import resumedata from "./cv.json";
import React, { useEffect, useState, } from 'react';

// Educations
const DisplayResumeData = () => {
    const [theme, setTheme] = useState('light'); // Define theme state here

  useEffect(() => {
    // Fetch cv.json and set cvData here...

    // Listen for secret code
    const handleKeyDown = (event) => {
      if (event.key === 'SecretCode') {
        setTheme(theme === 'light' ? 'dark' : 'light');
      }
    };
    window.addEventListener('keydown', handleKeyDown);

    // Clean up event listener
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [theme]);

  // Apply different styles based on the theme
  const style = theme === 'light' ? { backgroundColor: 'white' } : { backgroundColor: 'black', color: 'white' };
    return (
        <div>
            <div>
            <h1 onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}>
        CV Info
      </h1>
      {secretMessageVisible && <p>This is a secret message!</p>}
            <ul>
                {resumedata.arbetslivserfarenheter.map((arbetslivserfarenheter) => (
                    <li key={arbetslivserfarenheter.id}>
                        <h2> {arbetslivserfarenheter.name}</h2>
                        <p> {arbetslivserfarenheter.description}</p>
                        
                    </li>
                    ))}
                </ul>
            </div>
            <div>
            <h1>Utbildningar</h1>
            <ul>
             {resumedata.utbildningar.map((utbildningar) => (
                 <li key={utbildningar.id}>
                     <h2>{utbildningar.name}</h2>
                     <p>{utbildningar.description}</p>
                     
                 </li>
                 ))}
             </ul>
         </div>
         
         </div>
    );
};

export default DisplayResumeData;
